import React, { Component } from 'react'
import "./login.css"
export default class Login extends Component {

    state={
        username: '',
        password: '',
    }
    onChangeUser = (e)=>{
        this.setState({
          [e.target.name] : e.target.value
        })
      }
      onChangePassword = (e)=>{
        this.setState({
          [e.target.name] : e.target.value
        })
      }
      sumbit = (e) =>{
        e.preventDefault();
        this.props.login(this.state.username,this.state.password).bind(this);
      }

    render() {
        return (
            <div>
                <form  onSubmit={this.submit}>
                    <input type="text" name = "username" placeholder="Login" onChange={this.onChangeUser} />
                    <input type="password" name = "password" placeholder="password" onChange={this.onChangePassword} />
                    <input type="submit" value="Log in" />
                </form>
            </div>
        )
    }
}
