import React, { Component } from 'react'
import axios from 'axios'
import AuthenticationService from "./service/AuthenticationService"
export default class App extends Component {
  state={
    username: '',
    password: '',
    showSuccessMessage: false,
    hasLoginFailed: false
  }


  login = (e) =>{
    e.preventDefault();
    AuthenticationService
    .executeJwtAuthenticationService(this.state.username, this.state.password)
    .then((response) => {
        AuthenticationService.registerSuccessfulLoginForJwt(this.state.username, response.headers.authorization)
        this.setState({password:''})
    }).catch(() => {
        this.setState({ showSuccessMessage: false })
        this.setState({ hasLoginFailed: true })
        this.setState({password: ''})
    })
  }
  Fetch = () =>{
    axios.get('http://localhost:8080/quests')
    .then( response => console.log(response.data))
  }
  render() {
    return (
      <div>
        <form  onSubmit={this.login}>
        <input type="text" name = "username" placeholder="Login" onChange={this.onChangeUser} />
        <input type="password" name = "password" placeholder="password" onChange={this.onChangePassword} />
        <input type="submit" value="Log in" />
        </form>
        <button onClick={() => console.log(AuthenticationService.isUserLoggedIn())}>Check</button>
        <button onClick={this.Fetch}>Quests</button>
        <button onClick={AuthenticationService.logout}>Logout</button>

      </div>
    )
  }
}
